import { useState } from 'react';
import './Register.css';

function Register() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'peer',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Registering:', formData); // TODO: Integrate with backend
  };

  return (
    <>
      <video autoPlay muted loop className="register-bg-video">
        <source src="/videos/register.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <div className="register-container">
        <form onSubmit={handleSubmit} className="register-form">
          <h2>üõ°Ô∏è Join Niranjan Peer Space</h2>
          <p>Create your account to access the community.</p>

          <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
          <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
          <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />

          <select name="role" value={formData.role} onChange={handleChange}>
            <option value="peer">Peer</option>
            <option value="guest">Guest</option>
            <option value="admin">Admin</option>
          </select>

          <button type="submit">üöÄ Register</button>
        </form>
      </div>
    </>
  );
}

export default Register;
