import React, { useState } from 'react';
import './AdminConsole.css';

function AdminConsole() {
  const [tab, setTab] = useState('users');
  const [settingsOpen, setSettingsOpen] = useState(false);

  const mockUsers = [
    { id: 1, name: 'Alice', role: 'peer' },
    { id: 2, name: 'Bob', role: 'admin' },
    { id: 3, name: 'Charlie', role: 'peer' },
  ];

  const renderContent = () => {
    switch (tab) {
      case 'users':
        return (
          <div className="admin-section">
            <h3>👥 Registered Users</h3>
            {mockUsers.map((user) => (
              <div key={user.id} className="admin-user-card">
                <span>{user.name} — {user.role}</span>
                <div>
                  {user.role !== 'admin' && (
                    <button className="admin-btn">Promote</button>
                  )}
                  <button className="admin-btn remove">Remove</button>
                </div>
              </div>
            ))}
          </div>
        );
      case 'requests':
        return (
          <div className="admin-section">
            <h3>🆕 New Access Requests</h3>
            <p>No pending requests.</p>
          </div>
        );
      case 'chat':
        return (
          <div className="admin-section">
            <h3>💬 Peer Chat</h3>
            <p>Live messaging system coming soon.</p>
          </div>
        );
      case 'settings':
        return (
          <div className="admin-section">
            <h3>⚙️ Settings</h3>
            <p>Use actions to manage user roles or sessions.</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <>
      <video autoPlay muted loop className="bg-video">
        <source src="/videos/admin.mp4" type="video/mp4" />
      </video>

      <div className="admin-wrapper">
        <div className="admin-panel">
          <div className="admin-header">
            <h2>🛠 Admin Console</h2>
            <div className="admin-tabs">
              <button onClick={() => setTab('users')}>Users</button>
              <button onClick={() => setTab('requests')}>Requests</button>
              <button onClick={() => setTab('chat')}>Chat</button>
              <div className="settings-dropdown">
                <button onClick={() => setSettingsOpen(!settingsOpen)}>⚙</button>
                {settingsOpen && (
                  <div className="dropdown-menu">
                    <div onClick={() => setTab('settings')}>General</div>
                    <div onClick={() => alert('Logging out...')}>Log Out</div>
                  </div>
                )}
              </div>
            </div>
          </div>

          <div className="admin-body">
            {renderContent()}
          </div>
        </div>
      </div>
    </>
  );
}

export default AdminConsole;
