import React, { useState } from 'react';
import './AdminConsole.css';

function Admin() {
  const [tab, setTab] = useState('users');
  const [settingsOpen, setSettingsOpen] = useState(false);

  const mockUsers = [
    { id: 1, name: 'Alice', role: 'peer' },
    { id: 2, name: 'Bob', role: 'admin' },
    { id: 3, name: 'Charlie', role: 'peer' },
  ];

  const renderContent = () => {
    switch (tab) {
      case 'users':
        return (
          <div className="admin-section">
            <h3>üë• Registered Users</h3>
            {mockUsers.map(user => (
              <div key={user.id} className="admin-user-card">
                <span>{user.name} - {user.role}</span>
                <div>
                  {user.role !== 'admin' && (
                    <button className="admin-btn">Promote to Admin</button>
                  )}
                  <button className="admin-btn remove">Remove</button>
                </div>
              </div>
            ))}
          </div>
        );
      case 'requests':
        return (
          <div className="admin-section">
            <h3>üÜï New Access Requests</h3>
            <p>No pending requests.</p>
          </div>
        );
      case 'chat':
        return (
          <div className="admin-section">
            <h3>üí¨ Admin Chat Console</h3>
            <p>Chat system for peer communication will be implemented here.</p>
          </div>
        );
      default:
        return (
          <div className="admin-section">
            <h3>‚öôÔ∏è Settings</h3>
            <p>Select actions from dropdown.</p>
          </div>
        );
    }
  };

  return (
    <>
      <video autoPlay muted loop className="bg-video">
        <source src="/videos/admin.mp4" type="video/mp4" />
      </video>

      <div className="admin-container">
        <div className="admin-header">
          <h2>üõ† Admin Console</h2>
          <div className="admin-tabs">
            <button onClick={() => setTab('users')}>Users</button>
            <button onClick={() => setTab('requests')}>Requests</button>
            <button onClick={() => setTab('chat')}>Chat</button>
            <div className="settings-dropdown">
              <button onClick={() => setSettingsOpen(!settingsOpen)}>Settings ‚öô</button>
              {settingsOpen && (
                <div className="dropdown-menu">
                  <div onClick={() => setTab('settings')}>General Settings</div>
                  <div onClick={() => alert('Logged out')}>Log Out</div>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="admin-content">{renderContent()}</div>
      </div>
    </>
  );
}

export default Admin;
