import { BrowserRouter as Router, Routes, Route, NavLink, useLocation } from 'react-router-dom';
import { useState } from 'react';
import { AnimatePresence } from 'framer-motion';

import Home from './pages/Home';
import PersonalDetails from './pages/PersonalDetails';
import Experience from './pages/Experience';
import Education from './pages/Education';
import Skills from './pages/Skills';
import Projects from './pages/Projects';

import './App.css';

function AnimatedRoutes({ darkMode }) {
  const location = useLocation();

  return (
    <AnimatePresence mode="wait">
      <Routes location={location} key={location.pathname}>
        <Route path="/" element={<Home darkMode={darkMode} />} />
        <Route path="/personal" element={<PersonalDetails darkMode={darkMode} />} />
        <Route path="/experience" element={<Experience darkMode={darkMode} />} />
        <Route path="/education" element={<Education darkMode={darkMode} />} />
        <Route path="/skills" element={<Skills darkMode={darkMode} />} />
        <Route path="/projects" element={<Projects darkMode={darkMode} />} />
      </Routes>
    </AnimatePresence>
  );
}

function App() {
  const [darkMode, setDarkMode] = useState(true);

  return (
    <Router>
      <div className={darkMode ? 'dark' : 'light'}>
        {/* Navigation */}
        <nav className="navbar">
          <div className="nav-left">
            <NavLink to="/" className={({ isActive }) => isActive ? 'active-link' : ''}>Home</NavLink>
            <NavLink to="/personal" className={({ isActive }) => isActive ? 'active-link' : ''}>Personal</NavLink>
            <NavLink to="/education" className={({ isActive }) => isActive ? 'active-link' : ''}>Education</NavLink>
            <NavLink to="/experience" className={({ isActive }) => isActive ? 'active-link' : ''}>Experience</NavLink>
            <NavLink to="/skills" className={({ isActive }) => isActive ? 'active-link' : ''}>Skills</NavLink>
            <NavLink to="/projects" className={({ isActive }) => isActive ? 'active-link' : ''}>Projects</NavLink>
          </div>

          {/* Top-right Profile Picture */}
          <div className="nav-profile">
            <NavLink to="/personal">
              <img
                src="/images/profile_pic_Niranjan.jpg"
                alt="Niranjan"
                className="profile-pic"
              />
            </NavLink>
          </div>

          {/* Dark/Light Toggle */}
          <button className="theme-toggle" onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? 'üåô' : '‚òÄÔ∏è'}
          </button>
        </nav>

        {/* Main Content */}
        <main>
          <AnimatedRoutes darkMode={darkMode} />
        </main>
      </div>
    </Router>
  );
}

export default App;
