import React, { useEffect, useState } from 'react';
import './LoginSuccess.css';

function LoginSuccess() {
  const [peers, setPeers] = useState([]);
  const [requestsSent, setRequestsSent] = useState([]);
  const [notifications, setNotifications] = useState([
    { id: 1, message: '‚úÖ Alice accepted your chat request.' },
    { id: 2, message: '‚úÖ Charlie accepted your chat request.' },
  ]);

  // üß† Fetch real peers from backend
  useEffect(() => {
    const fetchPeers = async () => {
      const token = localStorage.getItem('token');

      try {
        const res = await fetch('http://localhost:5000/api/auth/users', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) {
          throw new Error('Failed to fetch peers');
        }

        const data = await res.json();
        setPeers(data);
      } catch (err) {
        console.error('Error fetching peers:', err);
      }
    };

    fetchPeers();
  }, []);

  const sendRequest = (peerId) => {
    if (!requestsSent.includes(peerId)) {
      setRequestsSent([...requestsSent, peerId]);
      alert('Chat request sent!');
    }
  };

  return (
    <div className="login-success-container">
      <h2>üéâ Welcome to the Peer Community</h2>

      <div className="community-section">
        <h3>Join a Community:</h3>
        <div className="community-buttons">
          <button>üë®‚Äçüíª Software Engineers</button>
          <button>üìä Data Analysts</button>
          <button>‚òÅÔ∏è Cloud Engineers</button>
        </div>
      </div>

      <div className="peer-list">
        <h3>Available Peers</h3>
        {peers.map((peer) => (
          <div key={peer._id} className="peer-card">
            <span>{peer.username} ‚Äî {peer.role}</span>
            <button
              disabled={requestsSent.includes(peer._id)}
              onClick={() => sendRequest(peer._id)}
            >
              {requestsSent.includes(peer._id) ? 'Request Sent' : 'Send Request'}
            </button>
          </div>
        ))}
      </div>

      <div className="notifications">
        <h3>üîî Notifications</h3>
        <ul>
          {notifications.map(note => (
            <li key={note.id}>{note.message}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default LoginSuccess;
