import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css';

function Register() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'peer',
  });

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        navigate('/register-success'); // ‚úÖ Go to success page
      } else {
        alert(data.message || 'Registration failed');
      }
    } catch (err) {
      alert('Server error: ' + err.message);
    }
  };

  return (
    <>
      <video autoPlay muted loop className="bg-video">
        <source src="/videos/register.mp4" type="video/mp4" />
      </video>

      <div className="register-container">
        <form onSubmit={handleSubmit} className="register-form">
          <h2>üõ°Ô∏è Join Niranjan Peer Space</h2>
          <p>Create your account to access the community.</p>

          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleChange}
            required
          />

          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />

          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />

          <select name="role" value={formData.role} onChange={handleChange}>
            <option value="peer">Peer</option>
            <option value="guest">Guest</option>
            <option value="admin">Admin</option>
          </select>

          <button type="submit">üöÄ Register</button>
        </form>
      </div>
    </>
  );
}

export default Register;
