// src/pages/Register.jsx
import { useState } from 'react';
import './Register.css'; // optional external CSS if needed

{/* ‚úÖ Fullscreen Background video for Personal page */}
      <video autoPlay muted loop className="bg-video">
        <source src="/videos/register.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

function Register() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'peer',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Send formData to backend
    console.log('Registering:', formData);
  };

  return (
    <div className="register-container">
      <h2>üîê Join the Network</h2>
      <p className="register-subtext">Create your personalized access to the Niranjan Peer Space.</p>

      <form onSubmit={handleSubmit} className="register-form">
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
          required
        />

        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />

        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />

        <select name="role" value={formData.role} onChange={handleChange}>
          <option value="peer">Peer</option>
          <option value="guest">Guest</option>
          <option value="admin">Admin</option>
        </select>

        <button type="submit">üöÄ Register</button>
      </form>
    </div>
  );
}

export default Register;
